package com.auxiliary.designer.command;public abstract class Commander {    protected String name;    protected Doer lili = new CodeWorker("lili");    protected Doer pipo = new TestWorker("pipo");    public abstract String commandSome();}class ToughCommander extends Commander{    public ToughCommander(String name) {        super.name = name;    }    @Override    public String commandSome() {        return lili.doSome() + pipo.doSome();    }}