package com.auxiliary.designer.visitor;public class Visitor implements IVisitor {    @Override    public void visit(CommonEmployee employee) {        this.getEmployeeInfo(employee);    }    private void getEmployeeInfo(CommonEmployee employee) {        System.out.println(this.getBaseInfo(employee)+employee.getJob());    }    @Override    public void visit(Manager manager) {        this.getManagerInfo(manager);    }    @Override    public void report() {        System.out.println("worker detail report！");    }    private void getManagerInfo(Manager manager) {        System.out.println(this.getBaseInfo(manager)+manager.getCaplity());    }    private String getBaseInfo(Employee employee){        return "name："+employee.getName()+" age:"+employee.getAge()+ "  responlity:"+employee.getResponlity();    }}