package com.auxiliary.manage.thread;import java.util.concurrent.CountDownLatch;import java.util.concurrent.TimeUnit;public class SystemCheckCallable<string> extends NameCallable<String> {    private CountDownLatch latch;    public SystemCheckCallable(String name, CountDownLatch latch) {        super.name = name;        this.latch = latch;    }    @Override    public String call() {        try {            TimeUnit.SECONDS.sleep(5);        } catch (InterruptedException e) {            e.printStackTrace();        }        latch.countDown();        return "系统检查";    }}