package com.auxiliary.tester;import java.util.concurrent.Callable;import java.util.concurrent.CountDownLatch;import java.util.concurrent.TimeUnit;public class CallableT implements Callable<String> {    private CountDownLatch latch;    public CallableT(CountDownLatch latch) {        this.latch = latch;    }    @Override    public String call() throws Exception {        System.out.println("------------------------call----------------------");        TimeUnit.SECONDS.sleep(10);        latch.countDown();        return "getall";    }}